function private_drawline(a,b,color,desired_line_width) % private_drawline(a,b,color,desired_line_width)% draw segment from a to b.% a and b are points, ie 2x1 vectorsglobal line_width;if nargin==2    color = 'k';    desired_line_width = line_width;end;if nargin==3    desired_line_width = line_width;end;line = [a;b];hold on;   % because usually you want to add a line to the current plotplot(line(:,1),line(:,2),color,'LineWidth',desired_line_width);% colors are:%% 'b'     blue          % 'g'     green % 'r'     red % 'c'     cyan  % 'm'     magenta % 'y'     yellow  % 'k'     black  